import React, { useState } from 'react';

export default function NewPlaceForm({addNewPlace}) {
  const [showForm, setShowForm] = useState(false)
  const [formData, setFormData] = useState({
    image: '',
    name: '',
    country: '',
    funFacts: ''
  });

  function handleChange(e) {
    setFormData({
      ...formData,
      [e.target.name] : e.target.value,
    })
  }
  const handleSubmit = () => {
    const newAnimal ={
      ...formData,
      likes: 0
    }
    fetch('http://localhost:3000/animals',{
      method: 'POST',
      headers: { 'Content-Type': 'application/json'},
      body: JSON.stringify(newAnimal)
    })
    .then(response=>response.json())
    .then(addNewPlace)
  };

  function onClick() {
    setShowForm(prev => !prev)
  }




  return (
    <>
    <button onClick ={onClick}className="addBtn" type="text" >Add New Destination</button>
    {showForm? <form onSubmit={handleSubmit} className="add-form" >
      <label>
        Image URL:
        <input
        name="image"
        className="form-input image"          
          type="text"
          value={formData.image}
          onChange={handleChange}
        />
      </label>
      <br />
      <label>
        Place Name:
        <input
        name="name"
        className="form-input name"
          type="text"
          value={formData.name}
          onChange={handleChange}
        />
      </label>
      <br />
      <label>
        Country:
        <input
        name="country"
        className="form-input country"
          type="text"
          value={formData.country}
          onChange={handleChange}
        />
      </label>
      <br />
      <label>
        Fun Facts:
        <textarea
        name="funFacts"
        className="form-input fun-facts"
          value={formData.funFacts}
          onChange={handleChange}
        />
      </label>
      <br />

      <br />
      <button type="submit" className="addDestBtn">Add </button>
    </form>: null}
    </>
  )


}
