import React from 'react'

export default function AnimalDetail({animal, addLikes,animalLikes,setAnimalLikes}) {
    const{name,image,hobbies,breed,id,likes} = animal


    function handleClick() {
    fetch(`http://localhost:3000/animals/${id}`,{
      method:'PATCH',
      headers: {
        'content-type': 'application/json',
        'accept': 'application/json',
      },
      body: JSON.stringify({"likes": animalLikes++})
    })
    .then(response => response.json())
    .then(addLikes)
  }

  return (
    <div className="animal-div">
      <img className="animal-image"src={image} alt={name} />
      <h2 className="animal-name">{name}</h2>
      <h4 className="animal-breed">{breed}</h4>
      <p className="animal-hobbies"> {hobbies}</p>
      <div className="line-div">
        <p className="line"></p>
      </div>
      <div className="like-comment">
        <button className="animal-likes" onClick={(e) => handleClick(e)}>{animalLikes} Likes</button>
        <button className="animal-comments">Comment</button>
      </div>

    </div>
  );
}
