import React, { useState } from 'react';

export default function NewPlaceForm({addNewPlace}) {
  const [showForm, setShowForm] = useState(false)
  const [formData, setFormData] = useState({
    image: '',
    name: '',
    country: '',
    funFacts: '',
    likes: 0
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    const newPlace ={
      image: '',
      name: '',
      country: '',
      funFacts: '',
      likes: 0
    }
    fetch('http://localhost:3000/places',{
      method: 'POST',
      headers: { 'Content-Type': 'application/json'},
      body: JSON.stringify(newPlace)
    })
    .then(response=>response.json())
    .then(addNewPlace)
  };

  function onClick() {
    setShowForm(prev => !prev)
  }
  function handleChange(e) {
    setFormData({
      ...formData,
      [e.target.name] : e.target.value,
    })
  }
  const Form=()=> (
    <form onSubmit={handleSubmit} className="add-form" showForm={showForm}>
      <label>
        Image URL:
        <input
        name="image"
        className="form-input image"          
          type="text"
          value={formData.image}
          onChange={handleChange}
        />
      </label>
      <br />
      <label>
        Place Name:
        <input
        name="name"
        className="form-input name"
          type="text"
          value={formData.name}
          onChange={handleChange}
        />
      </label>
      <br />
      <label>
        Country:
        <input
        name="country"
        className="form-input country"
          type="text"
          value={formData.country}
          onChange={handleChange}
        />
      </label>
      <br />
      <label>
        Fun Facts:
        <textarea
        name="funFacts"
        className="form-input fun-facts"
          value={formData.funFacts}
          onChange={handleChange}
        />
      </label>
      <br />

      <br />
      <button type="submit" className="addDestBtn">Add </button>
    </form>
  )

  return (
    <>
    <button onClick ={onClick}className="addBtn" >Add New Destination</button>
    {showForm? <Form /> : null}
    </>
  )
}
