import React, {useState, useEffect} from 'react';
import Header from './components/Header'
import Content from './components/Content'

function App() {
const [animals, setAnimals] = useState([])
const [search, setSearch] = useState("")
const [comments, setComments] = useState(animals.comments)

useEffect(() => {
  fetch('http://localhost:3000/animals')
  .then((response) =>response.json())
  .then(setAnimals)
},[])

function addNewAnimal(newAnimal) {
  setAnimals([
    newAnimal,
    ...animals
  ])
}

const filteredAnimals = animals.filter(animal => {
 return  animal.name.toLowerCase().includes(search.toLowerCase()) ||
 animal.breed.toLowerCase().includes(search.toLowerCase()) 
})

function addLikes(updatedLikes)  {
  setAnimals(animals.map(animal => animal.id === updatedLikes.id ? updatedLikes : animal))
}

  return (
    <div className="app">
      <Header addNewAnimal={addNewAnimal}
      search={search}
      setSearch={setSearch}/>
      <Content animals={filteredAnimals} addLikes={addLikes}/>
    </div>
  )
}

export default App;
