import React,{useState} from 'react'
import Paw from '../images/paw.png'
import AddComment from './AddComment'
import ShareButton from './ShareButton'

export default function AnimalDetail({animal, addLikes,setAnimals}) {
    const{name,image,hobbies,breed,id,likes,comments} = animal


    function handleClick() {
      fetch(`http://localhost:3000/animals/${id}`,{
        method:'PATCH',
        headers: {
          'content-type': 'application/json',
          'accept': 'application/json',
        },
        body: JSON.stringify({likes: animal.likes + 1})
      })
      .then(response => response.json())
      .then(addLikes)
    }

    function likesFunction() {
      if(likes === 1) {
        return "Like"
      }else  {
        return "Likes"
      }
    }

   function showCom() {

   }
   function numbeOfComments() {
    if(animal.comments.length === 1) {
      return "Comment"
    }else  {
      return "Comments"
    }
   }

  return (
    <>
    <div className="animal-div">
      <img className="animal-image"src={image} alt={name} />
      <h2 className="animal-name">{name}</h2>
      <h4 className="animal-breed">{breed}</h4>
      <p className="animal-hobbies"> {hobbies}</p>
      <div className="line-div">
        <p className="line"></p>
      </div>
      <div className="like-comment-share">
        <button 
          className="animal-likes" 
          onClick={handleClick}
          >
        <img src={Paw} alt="paw" className="paw-img"/>
            {likes} {likesFunction()}
        </button>
        <button className="animal-comments" onClick={showCom}>{numbeOfComments}Comments

        </button>
        <ShareButton animal={animal}/>
      </div>

      <AddComment setAnimals={setAnimals} animal={animal}/>

    </div>
    </>
  );
}
