import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';





    <>
      <Button variant="primary" onClick={handleShow}>
        Launch static backdrop modal
      </Button>

      <Modal
        show={show}
        onHide={handleClose}
        backdrop="static"
        keyboard={false}
      >
        <Modal.Header closeButton>
          <Modal.Title>Modal title</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          I will not close if you click outside me. Don't even try to press
          escape key.
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary">Understood</Button>
        </Modal.Footer>
      </Modal>
    </>









export default function AnimalForm({addNewAnimal}) {
  const [showForm, setShowForm] = useState(false)
  const [show, setShow] = useState(false);

  const [formData, setFormData] = useState({
    image: '',
    name: '',
    country: '',
    funFacts: ''
  });


  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  function handleChange(e) {
    setFormData({
      ...formData,
      [e.target.name] : e.target.value,
    })
  }
  const handleSubmit = () => {
    const newAnimal ={
      ...formData,
      likes: 0
    }
    fetch('http://localhost:3000/animals',{
      method: 'POST',
      headers: { 'Content-Type': 'application/json'},
      body: JSON.stringify(newAnimal)
    })
    .then(response=>response.json())
    .then(addNewAnimal)
  };

  function onClick() {
    setShowForm(prev => !prev)
  }


  return (
    <>

    <Button onClick={handleShow} variant="primary" className="addBtn" type="text" >Add New Pet</Button>
    {showForm? <form onSubmit={handleSubmit} className="add-form" >
      <label>
        Image URL:
        <input
        name="image"
        className="form-input image"          
          type="text"
          value={formData.image}
          onChange={handleChange}
        />
      </label>
      <br />
      <label>
        Place Name:
        <input
        name="name"
        className="form-input name"
          type="text"
          value={formData.name}
          onChange={handleChange}
        />
      </label>
      <br />
      <br />
      <label>
        Hobbies:
        <textarea
        name="funFacts"
        className="form-input fun-facts"
          value={formData.funFacts}
          onChange={handleChange}
        />
      </label>
      <br />

      <br />
      <button type="submit" className="addPetBtn">Add </button>
    </form>: null}
    </>
  )


}
