import React, { useState } from 'react'

export default function AddComment({animal, setAnimals}) {
  const [newReply, setNewReply] = useState("")
  const [showReply, setshowReply] = useState(false)


  function handleReplyChange(event) {
    setNewReply(event.target.value)
  }

  function handleReplySubmit(event) {

    fetch(`http://localhost:3000/animals/${animal.id}`, {
      method: "PATCH",
      headers: {
        "content-type": "application/json",
        accept: "application/json"
      },
      body: JSON.stringify(
        //{comments: [...animal.comments, [newReply]]}

        )
    })
    .then(response => response.json())
    .then(updatedComments => {
        const updatedReplyArray = animal.map(currentReply => {
            if(currentReply.id === updatedReply.id) {
                return updatedReplies
            } else {
                return currentReply
            }
        })
        setAnimals(updatedReplyArray)
    })
    setNewReply("")
    setshowReply(true)
  }



  return (
    <div>
      <form onSubmit={handleReplySubmit} >
          {animal.comments.map((comments, index) => (
            <li className="comments-list" key={index}>{comments} </li>
          ))}
          {showReply && <li>{newReply}</li>}
          <div className='comment-form'>
            <textarea 
              type="text" 
              value={newReply} 
              onChange={handleReplyChange} 
              placeholder="Write a comment..."
              className="type-comment"
            />
            <button className="post-comment-btn" type="submit">Post</button>
          </div>
      </form>

    </div>
  )
}
