import React from "react";

export default function Fetch({animal, newComment, }) {
  return fetch(`http://localhost:3000/animals/${animal.id}`, {
    method: "PATCH",
    headers: {
      "content-type": "application/json",
      accept: "application/json",
    },
    body: JSON.stringify({ comments: [...animal.comments, [newComment]] }),
  })
    .then((response) => response.json())
    .then((updatedAnimals) => {
      const updatedAnimalsArray = animal.map((currentAnimals) => {
        if (currentAnimals.id === updatedAnimals.id) {
          return updatedAnimals;
        } else {
          return currentAnimals;
        }
      });
      setanimals(updatedAnimalsArray);
    });
}
