import React, { useState } from 'react';

export default function NewPlaceForm({ addPlace }) {
  const [showForm, setShowForm] = useState(true)
  const [formData, setFormData] = useState({
    image: '',
    name: '',
    country: '',
    funFacts: '',
    likes: 0
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    addPlace(formData);
    setFormData({
      image: '',
      name: '',
      country: '',
      funFacts: '',
      likes: 0
    });
  };

  function onClick() {
    setShowForm(prev => !prev)
  }

  const Form=()=> (
    <form onSubmit={handleSubmit} className="add-form" showForm={showForm}>
      <label>
        Image URL:
        <input
        className="form-input image"          
          type="text"
          value={formData.image}
          onChange={e => setFormData({ ...formData, image: e.target.value })}
        />
      </label>
      <br />
      <label>
        Place Name:
        <input
        className="form-input name"
          type="text"
          value={formData.name}
          onChange={e => setFormData({ ...formData, name: e.target.value })}
        />
      </label>
      <br />
      <label>
        Country:
        <input
        className="form-input country"
          type="text"
          value={formData.country}
          onChange={e => setFormData({ ...formData, country: e.target.value })}
        />
      </label>
      <br />
      <label>
        Fun Facts:
        <textarea
        className="form-input fun-facts"
          value={formData.funFacts}
          onChange={e => setFormData({ ...formData, funFacts: e.target.value })}
        />
      </label>
      <br />
      <label>
        Likes:
        <input
        className="form-input likes"
          type="number"
          value={formData.likes}
          onChange={e => setFormData({ ...formData, likes: e.target.value })}
        />
      </label>
      <br />
      <button type="submit" className="addDestBtn">Add New Destination</button>
    </form>
  )
  return (
    <>
    <button onClick ={onClick}className="addBtn" >Add Destination</button>
    {showForm? <Form /> : null}
    </>
  )

}
