import React, { useState } from 'react'

export default function AddComment({animal, setAnimals}) {
  const [newComment, setNewComment] = useState("")
  const [showComment, setShowComment] = useState(false)


  function handleCommentChange(event) {
    setNewComment(event.target.value)
  }

  function handleCommentSubmit(event) {

    fetch(`http://localhost:3000/animals/${animal.id}`, {
      method: "PATCH",
      headers: {
        "content-type": "application/json",
        accept: "application/json"
      },
      body: JSON.stringify(
        //{comments: [...animal.comments, [newComment]]}
        { comments: [...animal.comments, {text: newComment, likes:0, reply: [], id: animal.comments.length +1}] }
        )
    })
    .then(response => response.json())
    .then(updatedAnimals => {
        const updatedAnimalsArray = animal.map(currentAnimals => {
            if(currentAnimals.id === updatedAnimals.id) {
                return updatedAnimals
            } else {
                return currentAnimals
            }
        })
        setAnimals(updatedAnimalsArray)
    })
    setNewComment("")
    setShowComment(true)
  }



  return (
    <div>
    <form onSubmit={handleCommentSubmit}>
        {animal.comments.map((comments, index) => (
          <li className="comments-list" key={index}>{comments}</li>
        ))}
        {showComment && <li>{newComment}</li>}

        <input 
          type="text" 
          value={newComment} 
          onChange={handleCommentChange} 
          placeholder="Write a comment..."
          className="type-comment"
        />

      <button className="post" type="submit">Post</button>
    </form>
    <ul>
    </ul>
    </div>
  )
}
