import React,{useState, useEffect} from 'react'
import News from './News'

export default function NewsContainer() {
    const[news, setNews]= useState([])

    useEffect(() => {
        fetch('https://v2.jokeapi.dev/joke/Programming,Miscellaneous,Pun?blacklistFlags=nsfw,religious,political,racist,sexist,explicit&type=single&amount=10')
        //fetch('http://www.boredapi.com/api/activity?type=recreational')
        .then(response => response.json())
        .then(setNews)
    },[])

    const activities = Object.keys(news).map(function(key) {
      return <option value={key}>{news[key]}</option>
  });

  return (
    activities.map(activity => (
      <News 
      key={activity.id}
      news={activity} />

    )) 
  )
}
