import React, { useState } from 'react'

export default function AddComment({animal, setAnimals}) {
  const [newComment, setNewComment] = useState("")

  function handleCommentChange(event) {
    setNewComment(event.target.value)
  }

  function handleCommentSubmit(event) {
    event.preventDefault()

    fetch(`http://localhost:3000/animals/${animal.id}`, {
      method: "PATCH",
      headers: {
        "content-type": "application/json",
        'accept': "application/json"
      },
      body: JSON.stringify({
        comments: [
          [...animal.comments].unshift(newComment),
          ]

      })
    })
    .then(response => response.json())
    .then(updatedAnimal => {
        const updatedAnimalsArray = animal.map(currentAnimal => {
            if(currentAnimal.id === updatedAnimal.id) {
                return updatedAnimal
            } else {
                return currentAnimal
            }
        })
        setAnimals(updatedAnimalsArray)
    })
    setNewComment("")
  }

  return (
    <form onSubmit={handleCommentSubmit}>
      <label>
        Add a comment:
        <input 
          type="text" 
          value={newComment} 
          onChange={handleCommentChange} 
          placeholder="Write a comment..."
        />
      </label>
      <button type="submit">Post</button>
    </form>
  )
}
