import React, {useState, useEffect} from 'react'
import CatFact from './CatFact'
import CatsHeader from './CatsHeader'
import FeaturedCat from './FeaturedCat'
import FeaturedHeader from './FeaturedHeader'

export default function CatFactsList() {
  const [facts, setFacts] = useState([])
  const [featuredCat, setFeaturedCat] = useState([])

  useEffect(() =>{
    fetch('https://cat-fact.herokuapp.com/facts')
    .then((response) =>response.json())
    .then(setFacts)
  },[])

useEffect(() =>{
    fetch('https://cataas.com/cat?width=250')
    .then((response) => response.json())
    .then(setFeaturedCat)
},[])


  return ( 
    <div className="cats">
      <CatsHeader />
      <ol className="cat-fact-list" >

          {
            facts.map(fact => (
              <CatFact key={fact.text} fact={fact}/>
            ))
          }
      </ol>
      <FeaturedHeader />
      {
        featuredCat.map(cat => (<FeaturedCat key={cat.id} fe/>))
      }

    </div>
  )
}
