import consumer from "./consumer"

const sendMessage = (channel, content, user_id, conversation_id) => {
  channel.perform('send_message', { content, user_id, conversation_id });
};

const createChatChannel = (conversation_id, receivedMessageCallback, user_id) => {
  console.log('Creating chat channel for conversation ID:', conversation_id);

  const cable = ActionCable.createConsumer('ws://localhost:3002/cable');
  console.log('Action Cable connection:', cable.connection);

  const channel = consumer.subscriptions.create({
    channel: "ChatChannel",
    conversation_id: conversation_id,
    user_id: user_id 
  }, {
    connected() {
      console.log("Connected to chat channel");
    },

    disconnected() {
      console.log("Disconnected from chat channel");
    },

    received(data) {
      console.log('Received message:', data);
      receivedMessageCallback(data);
    }
  });

  console.log('Chat channel:', channel);

  return { channel, sendMessage: (content, user_id) => sendMessage(channel, content, user_id, conversation_id) };
};

export default createChatChannel;
