import React, { useState } from "react";
import "./Auth.css";

export default function (props) {
  let [authMode, setAuthMode] = useState("signin");
  let [signInForm, setSignInForm] = useState({
    email: "",
    password: "",
  });
  let [signUpForm, setSignUpForm] = useState({
    fullName: "",
    email: "",
    password: "",
  });

  let [userCredentials, setUserCredentials] = useState({
    email: "",
    password: ""
  });


  const handleSignInFormChange = (event) => {
    const { name, value } = event.target;
    setSignInForm((prevState) => ({ ...prevState, [name]: value }));
  };

  const handleSignUpFormChange = (event) => {
    const { name, value } = event.target;
    setSignUpForm((prevState) => ({ ...prevState, [name]: value }));
  };

  const handleSignInSubmit = (event) => {
    event.preventDefault();
    fetch("http://localhost:3000/signin", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify(userCredentials)
    })
      .then(response => {
        if (response.ok) {
          // User is authenticated - redirect to the content page
          props.history.push("/");
        } else {
          // Authentication failed - display error message
          throw new Error("Email not found");
        }
      })
      .catch(error => {
        console.error(error);
        // Display error message to the user
      });
  };


  const handleSignUpSubmit = (event) => {
    event.preventDefault();
    fetch("http://localhost:3000/signup", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify(signUpForm)
    })
      .then(response => {
        if (response.ok) {
          // User is registered - redirect to the content page
          props.history.push("/");
        } else {
          // Registration failed - display error message
          throw new Error("Registration failed");
        }
      })
      .catch(error => {
        console.error(error);
        // Display error message to the user
      });
  };


  const changeAuthMode = () => {
    setAuthMode(authMode === "signin" ? "signup" : "signin");
  };

  if (authMode === "signin") {
    return (
      <div className="login-page">
        <div className="Auth-form-container">
          <form className="Auth-form" onSubmit={handleSignInSubmit}>
            <div className="Auth-form-content">
              <h3 className="Auth-form-title">Sign In</h3>
              <div className="text-center">
                Not registered yet?{" "}
                <span className="link-primary" onClick={changeAuthMode}>
                  Sign Up
                </span>
              </div>
              <div className="form-group mt-3">
                <label>Email address</label>
                <input
                  type="email"
                  className="form-control mt-1"
                  placeholder="Enter email"
                  name="email"
                  value={signInForm.email}
                  onChange={handleSignInFormChange}
                />
              </div>
              <div className="form-group mt-3">
                <label>Password</label>
                <input
                  type="password"
                  className="form-control mt-1"
                  placeholder="Enter password"
                  name="password"
                  value={signInForm.password}
                  onChange={handleSignInFormChange}
                />
              </div>
              <div className="d-grid gap-2 mt-3">
                <button type="submit" className="btn btn-primary">
                  Submit
                </button>
              </div>
              <p className="text-center mt-2"></p>
            </div>
          </form>
        </div>
      </div>
    );
  }


  return (
    <div className="signup-page">

    <div className="Auth-form-container">
      <form className="Auth-form" onSubmit={handleSignUpSubmit}>
        <div className="Auth-form-content">
          <h3 className="Auth-form-title">Sign In</h3>
          <div className="text-center">
            Already registered?{" "}
            <span className="link-primary" onClick={changeAuthMode}>
              Sign In
            </span>
          </div>
          <div className="form-group mt-3">
            <label>Full Name</label>
            <input
              type="email"
              className="form-control mt-1"
              placeholder="e.g Jane Doe"
              value={signUpForm.fullName}
            onChange={handleSignUpFormChange}
            />
          </div>
          <div className="form-group mt-3">
            <label>Email address</label>
            <input
              type="email"
              className="form-control mt-1"
              placeholder="Email Address"
              value={signUpForm.email}
                  onChange={handleSignUpFormChange}
            />
          </div>
          <div className="form-group mt-3">
            <label>Password</label>
            <input
              type="password"
              className="form-control mt-1"
              placeholder="Password"
              value={signUpForm.password}
                  onChange={handleSignUpFormChange}
            />
          </div>
          <div className="d-grid gap-2 mt-3">
            <button type="submit" className="btn btn-primary">
              Submit
            </button>
          </div>
          <p className="text-center mt-2">
          </p>
        </div>
      </form>
    </div>
    </div>
  );
}
