import React,{useState} from 'react'
import Animal from './Animal'


export default function AnimalList({animals,addLikes}) {
  const [displayedAnimals, setDisplayedAnimals] = useState(animals)


    const allanimals =displayedAnimals.map((animal) => (
        <Animal 
           animal={animal}
           key={animal.id}
           className="animal-list"
           addLikes={addLikes}
        />
    ))


    function showMore() {
      setDisplayedAnimals(prevAnimals => prevAnimals.concat(animals.slice(prevAnimals.length, prevAnimals.length + 5)))
    }

  return (
   <div className="animal-list">
    <>
    {allanimals}
    <div className="add-more-div">
      <button className="addMoreBtn" onClick={showMore}>Show More</button>
    </div>
    </>
    </div>
  )
}
