import React,{useState} from 'react'
import Paw from '../images/paw.png'

export default function AnimalDetail({animal, addLikes}) {
    const{name,image,hobbies,breed,id,likes} = animal


    function handleClick() {
      fetch(`http://localhost:3000/animals/${id}`,{
        method:'PATCH',
        headers: {
          'content-type': 'application/json',
          'accept': 'application/json',
        },
        body: JSON.stringify({likes: animal.likes + 1})
      })
      .then(response => response.json())
      .then(addLikes)
    }

    function likesFunction() {
      if(likes === 1) {
        return "Like"
      }else  {
        return "Likes"
      }
    }

    function handleCommentChange(event) {
      setNewComment(event.target.value)
  }

  function handleCommentSubmit(event) {
    event.preventDefault()

    fetch(`http://localhost:3001/animals/${id}/comments`, {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
            'Accept': 'application/json'
        },
        body: JSON.stringify({
            text: newComment
        })
    })
    .then(response => response.json())
    .then(data => {
        setComments([...comments, data])
        setNewComment("")
    })
}


  return (
    <>
    <div className="animal-div">
      <img className="animal-image"src={image} alt={name} />
      <h2 className="animal-name">{name}</h2>
      <h4 className="animal-breed">{breed}</h4>
      <p className="animal-hobbies"> {hobbies}</p>
      <div className="line-div">
        <p className="line"></p>
      </div>
        <button 
        className="animal-likes" 
        onClick={handleClick}
        >
          <img src={Paw} alt="paw" className="paw-img"/>
          {likes} {likesFunction()}
        </button>
        <div className="comments-section">
            <form onSubmit={handleCommentSubmit}>
                <textarea value={newComment} onChange={handleCommentChange} placeholder="Add a comment"></textarea>
                <button type="submit">Post Comment</button>
            </form>
            <div className="comments-list">
                {comments.map(comment => (
                    <div className="comment" key={comment.id}>
                        {comment.text}
                    </div>
                ))}
            </div>
        </div>
    </div>
    </>
  );
}
