import React,{useState} from 'react'

export default function AnimalDetail({animal, addLikes}) {
    const{name,image,hobbies,breed,id,likes} = animal
    const [commentSection, setCommentSection] = useState("")
    const [commentsList, setCommentsList]= useState([])


   function submitCommentBtnClick() {
    setCommentsList(commentsList=> [...commentsList, commentSection])
   }


    function handleClick() {
    fetch(`http://localhost:3000/animals/${id}`,{
      method:'PATCH',
      headers: {
        'content-type': 'application/json',
        'accept': 'application/json',
      },
      body: JSON.stringify({likes: animal.likes + 1})
    })
    .then(response => response.json())
    .then(addLikes)
  }

  function likesFunction() {
    if(likes === 1) {
      return "Like"
    }else  {
      return "Likes"
    }
  }

  function handleChange(e) {
    setCommentSection(e.target.value)
  }
  return (
    <div className="animal-div">
      <img className="animal-image"src={image} alt={name} />
      <h2 className="animal-name">{name}</h2>
      <h4 className="animal-breed">{breed}</h4>
      <p className="animal-hobbies"> {hobbies}</p>
      <div className="line-div">
        <p className="line"></p>
      </div>
      <div className="like-comment">
        <button className="animal-likes" onClick={handleClick}>{likes} {likesFunction()}
        </button>
        <button className="animal-comments">Comment</button>
      </div>
      {commentsList.map(text => {

      })}
      <div className="comments">{commentSection}</div>
      <div className="comment-container" >
        <textarea className="comment-textarea" onChange={handleChange}></textarea>
        <button className="comment-button" onClick={submitCommentBtnClick}>Submit</button>
      </div>

    </div>
  );
}
