import { createConsumer } from '@rails/actioncable';

const getWebSocketURL = () => {
  return 'ws://localhost:3000/cable';
};

const consumer = createConsumer(getWebSocketURL());

const sendMessage = (channel, content, user_id) => {
  channel.perform('send_message', { content, user_id });
};

const createChatChannel = (receivedMessageCallback, user_id) => {
  console.log('Creating chat channel');

  const channel = consumer.subscriptions.create(
    {
      channel: 'ChatChannel',
      user_id: user_id,
    },
    {
      connected() {
        console.log('Connected to chat channel');
      },

      disconnected() {
        console.log('Disconnected from chat channel');
      },

      received(data) {
        console.log('Received message:', data);
        if (typeof receivedMessageCallback === 'function' && data.message) {
          receivedMessageCallback(data.message);
        } else {
          console.error('receivedMessageCallback is not a function or message is empty');
        }
      },

    }
  );

  const sendMessage = (content, user_id) => {
    channel.perform('send_message', { content, user_id });
  };

  return { sendMessage };
};


export default createChatChannel;
