import React from 'react'
import Search from './Search'
import Logo from '../images/logo.png'
import {Link} from 'react-router-dom'
import styled from 'styled-components'
import './Header.css'

import { useContext } from 'react';
import { UserContext } from '../contexts/UserContext';

export default function Header({ search, setSearch, setUser }) {
  const { user = null } = useContext(UserContext); // provide a default value for user


  const handleLogout = () => {
    fetch("http://localhost:3000/logout", { method: 'DELETE' })
      .then(() => {
        setUser(null);
      // navigate('/');
      })
      .catch((error) => console.error(error));
  };
  return (
    <div className="header-div">
      <Link to="/">
        <LogoImg src={Logo} alt="logo" />
      </Link>
      <Search search={search} setSearch={setSearch} />
      {user ? (
        <div className='header-links'>
          <Link to="/profile" className="nav-link">
            Hello {user.full_name}
          </Link>
          <button onClick={handleLogout} className="navBtn">
            Log Out
          </button>
        </div>
      ) : (
        <>
          <Link to="/login" className="navBtn">
            Login
          </Link>
          <Link to="/signup" className="nav-link">
            Sign Up
          </Link>
        </>
      )}
    </div>
  );


}


/*******************************
*   STYLED COMPONENTS          *
*******************************/

const LogoImg = styled.img`
  width:140px;
  @media (max-width:600px) {
    width:100px;
    position: absolute;
    top: 15px;
    left: 0;
  }
`
