import React, { useState } from 'react';

export default function SearchBar({ places, setFilteredPlaces }) {
  const [searchTerm, setSearchTerm] = useState('');

  const handleChange = (e) => {
    setSearchTerm(e.target.value);
    const filteredPlaces = places.filter(place =>
      place.name.toLowerCase().includes(e.target.value.toLowerCase())
    );
    setFilteredPlaces(filteredPlaces);
  };

  return (
    <div className="search-div">
        <div className="search-box">
            <div className="search">
                <div className="select-area">
                <i class="fas fa-map-marker-alt map_icon"></i>
                <div className="text"> Destination </div>
                <div className="text_and_icon">
                    <input 
                    type="text" 
                    className="search_text" 
                    id="search_text" 
                    placeholder="search destination"
                    value={searchTerm}
                    onChange={handleChange} />
                <i class="fas fa-search search_icon"></i>
                </div>
                </div>
            </div>
        </div>

    </div>
)
}
