import React, {useState} from 'react'
import Modal from 'react-bootstrap/Modal';
import { UserContext } from '../contexts/UserContext';
import { useContext } from 'react';



export default function UserDetails({ user_id, onClose }) {
    const { user = null } = useContext(UserContext);
    const [userDetails, setUserDetails] = useState(null);

    useEffect(() => {
      fetch(`http://localhost:3000/users/${user_id}`)
        .then((response) => response.json())
        .then((data) => {
          setUserDetails(data);
        })
        .catch((error) => {
          console.error(error);
        });
    }, [user_id]);

    if (!userDetails) {
      return null;
    }

    return (
      <div>
        <Modal
          show={true}
          onHide={onClose}
          backdrop="static"
          keyboard={false}
        >
          <Modal.Header closeButton>
            <Modal.Title>Profile</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <div>
              <img src={userDetails.profile_picture} />
              <h2>{userDetails.full_name}</h2>
              <button> Send Message</button>
            </div>
          </Modal.Body>
          <Modal.Footer>
            <button type="submit" className="addPetBtn">
              Add
            </button>
          </Modal.Footer>
        </Modal>
      </div>
    );
  }
