import React from 'react';

export default function ShareButton({ animal }) {
  const shareUrl = `http://localhost:3000/animals/${animal.id}`;

  function handleClick() {
    if (navigator.share) {
      navigator.share({
        title: animal.name,
        url: shareUrl,
      })
        .then(() => console.log('Successful share'))
        .catch((error) => console.log('Error sharing', error));
    } else {
      console.log("The Web Share API is not supported by this browser.");
    }
  }

  return (
    <button className="share-button" onClick={handleClick}>
      Share
    </button>
  );
}
