// import necessary components and libraries
import React, {useState, useEffect} from 'react';
import Header from './components/header/Header'
import Content from './components/content/Content'
import Footer from './components/Footer'
import Login from './Login'
import SignUp from './SignUp'
import {Routes, Route} from "react-router-dom"
import { useContext } from 'react'
import { UserContext } from './components/contexts/UserContext'


function App() {
  const [animals, setAnimals] = useState([]);
  const [search, setSearch] = useState("");
  const [user, setUser] = useState(null);

  useEffect(() => {
    fetch("localhost3000/animals")
      .then((response) => response.json())
      .then(setAnimals)
      .catch((error) => console.error(error));
  }, []);

  const filteredAnimals = animals.filter(
    (animal) =>
      animal.name.toLowerCase().includes(search.toLowerCase()) ||
      animal.breed.toLowerCase().includes(search.toLowerCase())
  );

  function addNewAnimal(newAnimal) {
    setAnimals([newAnimal, ...animals]);
  }

  function addLikes(updatedAnimal) {
    setAnimals((prevAnimals) => {
      return prevAnimals.map((prevAnimal) => {
        if (prevAnimal.id === updatedAnimal.id) {
          return { ...prevAnimal, likes: updatedAnimal.likes };
        } else {
          return prevAnimal;
        }
      });
    });
  }

  return (
    <div className="app">
      <Header addNewAnimal={addNewAnimal} search={search} setSearch={setSearch} />
      <Routes>
        <Route
          path="/login"
          element={<Login setUser={setUser} />}
        />
        <Route
          path="/signup"
          element={<SignUp setUser={setUser} />}
        />
        <Route
          path="/"
          element={user ? (
            <Content
              animals={filteredAnimals}
              addLikes={addLikes}
              setAnimals={setAnimals}
            />
          ) : (
            <SignUp setUser={setUser} />
          )}
        />
      </Routes>
      <Footer />
    </div>
  );
}


export default App;
