import React, { useState } from "react";
import { useNavigate } from "react-router-dom";


import "./Auth.css";
import Login from "./Login"
import SignUp from "./SignUp"

export default function Auth(props) {
const history = useNavigate()
  let [authMode, setAuthMode] = useState("signin");
  let [signInForm, setSignInForm] = useState({
    email: "",
    password: "",
  });
  let [signUpForm, setSignUpForm] = useState({
    full_name: "",
    email: "",
    password: ""
  });


  const handleSignUpSubmit = (event) => {
    event.preventDefault();
    fetch("http://localhost:3000/signup", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify(signUpForm)
    })
      .then(response => {
        if (response.ok) {
          // User is registered - redirect to the content page
          history.push("/");
        } else {
          // Registration failed - display error message
          throw new Error("Registration failed");
        }
      })
      .catch(error => {
        console.error(error);
        // Display error message to the user
      });
  };



  const handleSignInFormChange = (event) => {
    const { name, value } = event.target;
    setSignInForm((prevState) => ({ ...prevState, [name]: value }));
  };

  const handleSignUpFormChange = (event) => {
    const { name, value } = event.target;
    setSignUpForm((prevState) => ({ ...prevState, [name]: value }));
  };

  const changeAuthMode = () => {
    setAuthMode(authMode === "signin" ? "signup" : "signin");
  };

  if (authMode === "signin") {
    return (
      <Login 
      handleSignInSubmit={handleSignInSubmit}
      changeAuthMode={changeAuthMode}
      signInForm={signInForm}
      handleSignInFormChange={handleSignInFormChange}
      />
    );
  }
else 
  return (
    <SignUp 
    handleSignUpSubmit={handleSignUpSubmit}
    changeAuthMode={changeAuthMode}
    signUpForm={signUpForm}
    handleSignUpFormChange={handleSignUpFormChange}
    />
  );
}
