import React,{useState} from 'react'
import Paw from '../images/paw.png'

export default function AnimalDetail({animal, addLikes}) {
    const{name,image,hobbies,breed,id,likes} = animal
    const [comments, setComments] = useState([])
    const [newComment, setNewComment] = useState("")

    function handleClick() {
      fetch(`http://localhost:3000/animals/${id}`,{
        method:'PATCH',
        headers: {
          'content-type': 'application/json',
          'accept': 'application/json',
        },
        body: JSON.stringify({likes: animal.likes + 1})
      })
      .then(response => response.json())
      .then(addLikes)
    }

    function likesFunction() {
      if(likes === 1) {
        return "Like"
      }else  {
        return "Likes"
      }
    }

    function handleCommentChange(event) {
      setNewComment(event.target.value)
  }

  function handleCommentSubmit(event) {
      event.preventDefault()
      const newCommentObject = {
          id: comments.length + 1,
          text: newComment
      }
      setComments([...comments, newCommentObject])
      setNewComment("")
  }


  return (
    <>
    <div className="animal-div">
      <img className="animal-image"src={image} alt={name} />
      <h2 className="animal-name">{name}</h2>
      <h4 className="animal-breed">{breed}</h4>
      <p className="animal-hobbies"> {hobbies}</p>
      <div className="line-div">
        <p className="line"></p>
      </div>
        <button 
        className="animal-likes" 
        onClick={handleClick}
        >
          <img src={Paw} alt="paw" className="paw-img"/>
          {likes} {likesFunction()}
        </button>
       <div className="comments-section">
            <h5>Comments</h5>
            <form onSubmit={handleCommentSubmit}>
                <input 
                    type="text" 
                    value={newComment} 
                    onChange={handleCommentChange} 
                    placeholder="Add a comment..." 
                />
                <button type="submit">Post</button>
            </form>
            <ul>
                {comments.map(comment => (
                    <li key={comment.id}>{comment.text}</li>
                ))}
            </ul>
        </div> 
    </div>
    </>
  );
}
