import React, { useState, useEffect } from "react";
import createChatChannel from "../javascript/channels/chat_channel";
import "./ChatModal.css";
import { useContext } from 'react';
import { CableContext } from './CableContext';
import { useRef } from 'react';

const ChatModal = ({
  currentUser,
  author,
  handleClose,
  handleChatButtonClick,
  newMessage,
  setNewMessage,
}) => {

  const [messages, setMessages] = useState([]);
  const [inputValue, setInputValue] = useState("");
  const [chatChannel, setChatChannel] = useState(null);
  const [openConversations, setOpenConversations] = useState({});

  // useEffect(() => {
  //   if (currentUser?.id && author?.id) {
  //     const channel = createChatChannel(handleReceivedMessage, currentUser.id);
  //     setChatChannel(channel);
  //     if (newMessage) {
  //       channel.sendMessage(newMessage.content, currentUser?.id);
  //     }
  //   }
  // }, [currentUser, author, newMessage]);

  // useEffect(() => {
  //   if (newMessage) {
  //     handleReceivedMessage(newMessage);
  //   }
  // }, [newMessage]);

  const handleReceivedMessage = (message) => {
    console.log("Received message:", message);
    setMessages((prevMessages) => [...prevMessages, message]);
    handleChatButtonClick(author);
  };

  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };

  const handleSendMessage = () => {
    if (inputValue.trim()) {
      const newSentMessage = {
        content: inputValue,
        user_id: currentUser?.id,
      };

      setMessages((prevMessages) => [...prevMessages, newSentMessage]);

      if (!chatChannel) {
        const channel = createChatChannel(handleReceivedMessage, currentUser.id);
        setChatChannel(channel);
        channel.sendMessage(inputValue, currentUser?.id);
      } else {
        chatChannel.sendMessage(inputValue, currentUser?.id);
      }

      setInputValue("");
      console.log("New message sent:", newSentMessage.content);
    }
  };


  return (
    <>
      <section style={{ backgroundColor: "#eee" }} className="section">
        <div className="chatModal py-5">
          <div className="">
            <div className="">
              <div className="card">
                <div
                  className="card-header d-flex justify-content-between align-items-center p-3"
                  style={{ borderTop: "4px solid #ffa900" }}
                >
                  <h5 className="mb-0">Messages </h5>
                  <button onClick={handleClose}>Close</button>
                </div>
                <div
                  className="card-body"
                  data-mdb-perfect-scrollbar="true"
                  style={{ position: "relative", height: "300px" }}
                >
                  {/* <p>Message 1 </p> */}
                  {messages.map((message, index) => (
                    <div
                      key={index}
                      className={
                        message.user_id === currentUser.id
                          ? "currentUserMessage"
                          : "authorMessage"
                      }
                    >
                      <p> {message.content}</p>
                    </div>
                  ))}
                </div>
                <div className="card-footer text-muted d-flex justify-content-start align-items-center p-3">
                  <div className="input-group mb-0">
                    <input
                      type="text"
                      className="form-control"
                      placeholder="Type message"
                      aria-label="Recipient's username"
                      aria-describedby="button-addon2"
                      value={inputValue}
                      onChange={handleInputChange}
                    />
                    <button
                      className="btn btn-warning"
                      type="button"
                      style={{ paddingTop: ".55rem" }}
                      onClick={handleSendMessage}
                      disabled={!chatChannel}

                    >
                      Send
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    </>
  );
};

export default ChatModal;
