import React, { useState } from "react";
import "./Auth.css";
import Login from "./Login"
import SignUp from "./SignUp"

export default function Auth(props) {
  let [authMode, setAuthMode] = useState("signin");
  let [signInForm, setSignInForm] = useState({
    email: "",
    password: "",
  });
  let [signUpForm, setSignUpForm] = useState({
    email: "",
    password: "",
    password_confirmation: ""
  });

  let [userCredentials, setUserCredentials] = useState({
    email: "",
    password: ""
  });


  const handleSignInFormChange = (event) => {
    const { name, value } = event.target;
    setSignInForm((prevState) => ({ ...prevState, [name]: value }));
  };

  const handleSignUpFormChange = (event) => {
    const { name, value } = event.target;
    setSignUpForm((prevState) => ({ ...prevState, [name]: value }));
  };

  const handleSignInSubmit = (event) => {
    event.preventDefault();
    fetch("http://localhost:3000/signin", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify(userCredentials)
    })
      .then(response => {
        if (response.ok) {
          // User is authenticated - redirect to the content page
          props.history.push("/");
        } else {
          // Authentication failed - display error message
          throw new Error("Email not found");
        }
      })
      .catch(error => {
        console.error(error);
        // Display error message to the user
      });
  };


  const handleSignUpSubmit = (event) => {
    event.preventDefault();
    fetch("http://localhost:3000/signup", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify(signUpForm)
    })
      .then(response => {
        if (response.ok) {
          // User is registered - redirect to the content page
          props.history.push("/");
        } else {
          // Registration failed - display error message
          throw new Error("Registration failed");
        }
      })
      .catch(error => {
        console.error(error);
        // Display error message to the user
      });
  };


  const changeAuthMode = () => {
    setAuthMode(authMode === "signin" ? "signup" : "signin");
  };

  if (authMode === "signin") {
    return (
      <Login 
      handleSignInSubmit={handleSignInSubmit}
      changeAuthMode={changeAuthMode}
      signInForm={signInForm}
      handleSignInFormChange={handleSignInFormChange}
      />
    );
  }


  return (
    <SignUp 
    handleSignInSubmit={handleSignInSubmit}
    changeAuthMode={changeAuthMode}
    signUpForm={signInForm}
    handleSignInFormChange={handleSignInFormChange}
    />
  );
}
