import consumer from "./consumer"

const sendMessage = (channel, content, user_id) => {
  channel.perform('send_message', { content, user_id });
};

const createChatChannel = (conversation_id, receivedMessageCallback) => {
  const channel = consumer.subscriptions.create({
    channel: "ChatChannel",
    conversation_id: conversation_id
  }, {
    connected() {
      console.log("Connected to chat channel");
    },

    disconnected() {
      console.log("Disconnected from chat channel");
    },

    received(data) {
      receivedMessageCallback(data);
    }
  });

  return { channel, sendMessage: (content, user_id) => sendMessage(channel, content, user_id) };
};

export default createChatChannel;
