import React, {useState, useEffect} from 'react'
import Modal from 'react-bootstrap/Modal';
import { UserContext } from '../contexts/UserContext';
import { useContext } from 'react';



export default function UserDetails({ user_id}) {
    const [user, setUser] = useState(null);
    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    return (
      <div>
        {show && user && (
          <Modal show={true} onHide={handleClose} backdrop="static" keyboard={false}>
            <Modal.Header closeButton>
              <Modal.Title>Profile</Modal.Title>
            </Modal.Header>

            <Modal.Body>
              <div>
                {/* <img src={user.image} alt={user.full_name} /> */}
                <h2> Name: {user.full_name}</h2>
                <button>Send Message</button>
              </div>
            </Modal.Body>

            <Modal.Footer>
              <button type="submit" className="addPetBtn">
                Add
              </button>
            </Modal.Footer>
          </Modal>
        )}
      </div>
    );
  }
