import React from 'react'
import AnimalList from './AnimalList'
import CatSection from '../cats/CatSection'
import DogSection from '../dogs/DogSection'
import styled from 'styled-components'



export default function Content({animals,addLikes, setAnimals}) {
  return (
    <ContentDiv >
      <DogSection className="hide-on-mobile"/>
      <AnimalsList 
          animals={animals} 
          addLikes={addLikes}
          setAnimals={setAnimals} />
      <CatSection className="hide-on-mobile"/>
    </ContentDiv>
  )
}

const ContentDiv= styled.div 
`  display:grid;
grid-template-columns: auto  auto auto;
margin-top:30px;
position:relative;

@media screen and (max-width: 600px) {
  .hide-on-mobile {
      display: none;
  }
}
`
