import React, { useState } from 'react'


export default function AddComment({animal, setanimals}) {
  const [newComment, setnewComment] = useState("")
  const [showComment, setshowComment] = useState(false)
  const [likeComment, setLikeComment] = useState({})
  const [isLikeClicked, setIsLikeClicked] = useState(false)
  const [showReply, setShowReply] = useState({})
  const [newReply, setNewReply] = useState('')


  function updateAnimals(e) {
    e.preventDefault()
    if(newComment) {
      fetch(`http://localhost:3000/animals/${animal.id}`, {
          method: "PATCH",
          headers: {
            "content-type": "application/json",
            accept: "application/json",
          },
          body: JSON.stringify({ comments: 
            [...animal.comments, { comment: newComment, replies: [], likes: 0 }] }),
        })
          .then((response) => response.json())
          .then((updatedAnimals) => {
            const updatedAnimalsArray = [...animals];
            const index = updatedAnimalsArray.findIndex(currentAnimals => currentAnimals.id === updatedAnimals.id);
            updatedAnimalsArray[index] = updatedAnimals;
            setanimals(updatedAnimalsArray);
          })
      }
    setnewComment("")
    setshowComment(true)
  }

  function handleCommentChange(event) {
    setnewComment(event.target.value)
  }

  function handleCommentSubmit(e) {
    e.preventDefault()
    if(newComment && !isLikeClicked) {
      updateAnimals();
    }
    setIsLikeClicked(false);
    setnewComment("")
    setshowComment(true)
  }
  function likeComments(index) {
    setLikeComment(prev => {
      let newLikeComment = {...prev}
      newLikeComment[index] = !newLikeComment[index]
      return newLikeComment
    })

  }

  function showCommentReplies(index) {
    setShowReply(prev => {
      let isCommentShown = {...prev}
      isCommentShown[index] = !isCommentShown[index]
      return isCommentShown
    })
  }

  function handleReplyChange(e) {
    setNewReply(e.target.value)
  }

  function handleReplySubmit() {

  }
  return (
    <div>
      <form onSubmit={handleCommentSubmit} >
          {animal.comments.map((comments, index) => (
            <>
            <li className="comments-list" 
            key={index} 
            animal={animal}
            setanimals={setanimals}>
              {comments} 
              </li>
          <button 
          className="like-reply" 
          onClick={() => likeComments(index)}>
            {likeComment[index]? '♥' :'♡' }  
             Like
            </button>
            <button 
              className="like-reply"
              onClick={(e) =>showCommentReplies(index)}>⌨ Reply</button>
              {showReply && <li >{newReply}</li>}
              {showReply[index] ? (
                <div className="comment-form">
                  <textarea
                    type="text"
                    value={newReply}
                    onChange={handleReplyChange}
                    placeholder="Write a reply..."
                    className="type-reply"
                  />
                  <button
                    className="post-comment-btn replyBtn"
                    type="submit"
                    onClick={e => handleReplySubmit(e, index)}
                  >
                    Reply
                  </button>
                </div>
              ) : null}

            </>
          ))}
          {showComment && <li className="new-reply-li">{newComment}</li>}
          <div className='comment-form'>
            <textarea 
              type="text" 
              value={newComment} 
              onChange={handleCommentChange} 
              placeholder="Write a comment..."
              className="type-comment"
            />
            <button className="post-comment-btn" type="submit">Post</button>
          </div>
      </form>
    </div>
  )
}
