import React, { useState } from 'react'
//import Comment from './Comment'

export default function AddComment({animal, setanimals}) {
  const [newReply, setnewReply] = useState("")
  const [showReply, setshowReply] = useState(false)
  const [likeComment, setLikeComment] = useState({})
  const [isLikeClicked, setIsLikeClicked] = useState(false)


  function handleCommentChange(event) {
    setnewReply(event.target.value)
  }

  function handleCommentSubmit(e) {
    e.preventDefault()
    if(newReply && !isLikeClicked) {
      updateAnimals();
    }
    setIsLikeClicked(false);
    setnewReply("")
    setshowReply(true)
  }
  function likeComments(index) {
    setLikeComment(prev => {
      let newLikeComment = {...prev}
      newLikeComment[index] = !newLikeComment[index]
      return newLikeComment
    })
  }

    function updateAnimals(e) {
      e.preventDefault()
      if(newReply) {

        fetch(`http://localhost:3000/animals/${animal.id}`, {
          method: "PATCH",
          headers: {
            "content-type": "application/json",
            accept: "application/json"
          },
          body: JSON.stringify({comments: [...animal.comments, [newReply]]})
        })
        .then(response => response.json())
        .then(updatedAnimals => {
            const updatedAnimalsArray = animal.map(currentAnimals => {
                if(currentAnimals.id === updatedAnimals.id) {
                    return updatedAnimals
                } else {
                    return currentAnimals
                }
            })
            setanimals(updatedAnimalsArray)
        })
      }
      setnewReply("")
      setshowReply(true)
    }



  return (
    <div>
      <form onSubmit={handleCommentSubmit} >
          {animal.comments.map((comments,index) => (
            <>
            <li className="comments-list" 
            key={animal.id} 
            animal={animal}
            setanimals={setanimals}>
              {comments} 
              </li>
          <button 
          className="like-reply" 
          onClick={() => likeComments(index)}>
            {likeComment[index]? '♥' :'♡' } 
            Like
            </button>
            <button className="like-reply">↳ Reply</button>
            </>
          ))}
          {showReply && <li className="new-reply-li" key={animal.id}>{newReply}</li>}
          <div className='comment-form'>
            <textarea 
              type="text" 
              value={newReply} 
              onChange={handleCommentChange} 
              placeholder="Write a comment..."
              className="type-comment"
            />
            <button className="post-comment-btn" type="submit">Post</button>
          </div>
      </form>

    </div>
  )
}
