import React, { useState } from 'react';

export default function NewPlaceForm({ addPlace }) {
  const [formData, setFormData] = useState({
    image: '',
    name: '',
    country: '',
    funFacts: '',
    likes: 0
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    addPlace(formData);
    setFormData({
      image: '',
      name: '',
      country: '',
      funFacts: '',
      likes: 0
    });
  };

  return (
    <>
    <h3>Add Destination</h3>
    <form onSubmit={handleSubmit}>
      <label>
        Image URL:
        <input
        className="form-input"          
          type="text"
          value={formData.image}
          onChange={e => setFormData({ ...formData, image: e.target.value })}
        />
      </label>
      <br />
      <label>
        Place Name:
        <input
        className="form-input name"
          type="text"
          value={formData.name}
          onChange={e => setFormData({ ...formData, name: e.target.value })}
        />
      </label>
      <br />
      <label>
        Country:
        <input
        className="form-input country"
          type="text"
          value={formData.country}
          onChange={e => setFormData({ ...formData, country: e.target.value })}
        />
      </label>
      <br />
      <label>
        Fun Facts:
        <textarea
        className="form-input fun-facts"
          value={formData.funFacts}
          onChange={e => setFormData({ ...formData, funFacts: e.target.value })}
        />
      </label>
      <br />
      <label>
        Likes:
        <input
        className="form-input likes"
          type="number"
          value={formData.likes}
          onChange={e => setFormData({ ...formData, likes: e.target.value })}
        />
      </label>
      <br />
      <button type="submit">Add Place</button>
    </form>
    </>
  );
}
