  // const auth = firebase.auth() 
  // const firestore = firebase.firestore()

  import React, {useState} from 'react';
  import './App.css'
  import Header from './components/header/Header'
  import Content from './components/content/Content'
  import Footer from './components/Footer'
  import Login from './Login'
  import SignUp from './SignUp'
  import Profile from './Profile'
  import {Routes, Route} from "react-router-dom"
  import { UserContext } from './components/contexts/UserContext'
  import ChatModal from './components/content/ChatModal'
  import { CableProvider } from './components/content/CableContext';


export default function App() {
  const [animals, setAnimals] = useState([]);
  const [search, setSearch] = useState("");
  const [user, setUser] = useState(null);
  const [isChatModalOpen, setIsChatModalOpen] = useState(false);
  const [newMessage, setNewMessage] = useState(null);
  const [showChatModal, setShowChatModal] = useState(false);
  const [selectedAuthor, setSelectedAuthor] = useState(null);

  const handleChatButtonClick = (author, shouldOpen) => {
    if (shouldOpen) {
      setSelectedAuthor(author);
      setShowChatModal({ show: true, position: "fixed" });
    } else {
      setShowChatModal(false);
    }
    if (newMessage) {
      setNewMessage(null);
    }
  };

  const handleCloseChatModal = () => {
    setShowChatModal(false);
  };

  const toggleChatModal = () => {
    setIsChatModalOpen(!isChatModalOpen);
  };

  function addNewAnimal(newAnimal) {
    setAnimals([...animals, newAnimal]);
  }

  return (
    <CableProvider>

    <UserContext.Provider value={{ user, setUser }} >
      <div className="app">

        <Routes>
          <Route
            path="/login"
            element={<Login setUser={setUser} />}
          />
          <Route
            path="/signup"
            element={<SignUp setUser={setUser} />}
          />
            <Route
            path="/profile"
            element={<Profile setUser={setUser} />}/>
          <Route
            path="/"
            element={user ? (
              <>
              <Header 
                search={search} 
                setSearch={setSearch} 
                setUser={setUser}/>
              <Content
                search={search}
                animals={animals}
                setAnimals={setAnimals}
                addNewAnimal={addNewAnimal}
                setUser={setUser}
                newMessage={newMessage}
                setNewMessage={setNewMessage}
              />
               {/* Uncomment the ChatModal component and pass the required props */}
               {showChatModal && user && selectedAuthor && (
                  <ChatModal
                    currentUser={user}
                    author={selectedAuthor}
                    handleClose={handleCloseChatModal}
                    position={showChatModal.position}
                    handleChatButtonClick={handleChatButtonClick}
                    newMessage={newMessage}
                    setNewMessage={setNewMessage}
                  />
                )}
              <Footer />
              </>
            ) : (
              <div className="login-to-view" >
                <Header 
                    addNewAnimal={addNewAnimal} 
                    search={search} 
                    setSearch={setSearch} 
                    setUser={setUser}/>
                <div className="error-msg">You must be <br></br>logged in <br></br>to view this <br></br>content!</div>
              </div>

            )}
          />
        </Routes>
      </div>
    </UserContext.Provider>
    </CableProvider>
  );
}
